pragma solidity 0.8.17;

// Import of ERC20 standard
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

// Import of Ownable.
import "@openzeppelin/contracts/access/Ownable.sol";

import "./WID.sol";

import "../identity/EmployeeCard.sol";

/// @title Stacking ERC20 token used for WorkID.
/// @dev This contract can only be used by Governance contract.
/// @notice Any WorkID valid SBT holder can stack WID tokens to get governance voting powers.
contract SWID is ERC20, Ownable {

    /// @notice Store desposit informations.
    /// @dev StackTime and duration will be used for withdraw amount available calculation.
    struct WidDeposit {
        uint256 duration;
        uint256 stackTime;
        uint256 amountWID;
        uint256 amountSWID;
    }

    WID private immutable _widContract;
    mapping (address => mapping(uint256 => WidDeposit)) private _deposits;
    uint256 private _totalDeposits;

    event WIDStacked(uint256 depositId, address from, uint256 amountWID, uint256 duration, uint256 stackTime);
    event SWIDMinted(address to, uint256 amount);
    event DepositUnstacked(address from, uint256 depositId);
    event SWIDBurnt(uint256 amount);

    /// @notice ERC20 token for WID staking (symbol: sWID)
    /// @param _widContractAddress WID contract address.
    constructor(
        address _widContractAddress
    ) ERC20("Stacked WID", "SWID") {
        _widContract = WID(_widContractAddress);
    }

    /// @notice Receives
    /// @dev The amount of sWID minted depends of the staking duration.
    /// @param duration Stacking duration in months (must be 6, 12, 36 or 60).
    /// @param stacker The stacker address.
    /// @param amount Amount of WID to stack.
    function stackWID(uint256 duration, address stacker, uint256 amount) public onlyOwner {
        require(amount > 0, "SWID: You must send a positive amount of WID");
        require(duration == 15768000 || duration == 31536000 || duration == 94608000 || duration == 157680000, "SWID: Duration must be either 6 months, 1 year, 3 years or 5 years");

        uint256 amountSWID;
        // 6 months
        if (duration == 15768000) {
            amountSWID = (amount / 100) * 10;
        }
        // 1 year
        else if (duration == 31536000) {
            amountSWID = (amount / 100) * 20;
        }
        // 3 years
        else if (duration == 94608000) {
            amountSWID = (amount / 100) * 60;
        }
        // 5 years
        else {
            amountSWID = amount;
        }

        // Save the deposit.
        uint256 stackTime = block.timestamp;
        uint256 depositId = _totalDeposits++;
        _deposits[stacker][depositId] = WidDeposit(duration, stackTime, amount, amountSWID);

        // Transfers the amount stacked to this contract.
        _widContract.transferFrom(stacker, address(this), amount);

        // Mints new sWID depending of the amount of WID payed and the duration choosen
        _mint(stacker, amountSWID);

        emit WIDStacked(depositId, stacker, amount, duration, stackTime);
        emit SWIDMinted(stacker, amountSWID);
    }

    /// @notice Allows to unstack a specific deposit.
    /// @dev dApps will get the list of deposits through the WIDStack events.
    /// @param depositId The deposit to unstack
    /// @param stacker The stacker asking to unstack.
    function unstackWID(uint256 depositId, address stacker) public onlyOwner {
        require(_deposits[stacker][depositId].duration > 0, "SWID: The specified deposit doesn't exist.");
        require(_deposits[stacker][depositId].stackTime + _deposits[stacker][depositId].duration <= block.timestamp, "SWID: You can't unstack before stacking duration is fullfilled.");

        uint256 unstackAmount = _deposits[stacker][depositId].amountWID;
        uint256 burnAmount = _deposits[stacker][depositId].amountSWID;
        delete _deposits[stacker][depositId];

        // Transfer back the stacked WID.
        _widContract.transfer(stacker, unstackAmount);

         // Burn the corresponding SWID tokens.
        _burn(stacker, burnAmount);

        emit DepositUnstacked(stacker, unstackAmount);
        emit SWIDBurnt(burnAmount);
    }
}","keccak256":"0x0823d3ce5c2ea02eafe2a45f8c0fb31aff64d3c1b9bdf931d70c880888c4b435","license":"MIT"},"contracts/governance/WID.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

// Import of ERC20 standard
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

// Import of Ownable.
import "@openzeppelin/contracts/access/Ownable.sol";

contract WID is ERC20, Ownable {
    constructor() ERC20("WorkID Token", "WID") {}

    function mint(address recipient, uint256 amount) external onlyOwner {
        _mint(recipient, amount);
    }

    function burn(address from, uint256 amount) external onlyOwner {
        _burn(from, amount);
    }
}","keccak256":"0xba05fb2888257f5352e8755c475e24e613acda83c6f6924a86b883e17180e54d","license":"MIT"},"contracts/identity/EmployeeCard.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

// Import of ERC5484 token standard.
import "./token/ERC5484/ERC5484.sol";

/// @title An SBT for professionnal decentralized identity and proof of experience.
/// @author Bertrand Presles
/// @notice You can use this contract to generate digital ids for your employees that can also be used as proof of their work in your company
contract EmployeeCard is ERC5484 {

  /// @notice Mapping for token URIs
  mapping(uint256 => string) private _tokenURIs;

  /// @notice Mapping of token ids with end date.
  mapping(uint256 => uint256) private _tokenEndTimes;

  // Event when tokens are sent.
  event TokenReceived(address sender, uint256 amount);
  event CallReceived(address sender, uint256 amount, bytes data);
  event EmployeeCardMinted(address employee, uint256 tokenId);
  event VacationRightsCalculated(address employee);
  event EmployeeCardEnded(uint256 tokenId, uint256 endTime);

  constructor(string memory name, string memory symbol) ERC5484(name, symbol) {}

  /// @notice Gets the token URI for the passed token id.
  /// @dev Returns an URI for a given token ID. Revert if the token ID does not exist. May return an empty string.
  /// @param tokenId uint256 ID of the token to query
  /// @return The token URI 
  function tokenURI(uint256 tokenId) public view virtual override returns (string memory) {
      require(_exists(tokenId));
      return _tokenURIs[tokenId];
  }

  /// @notice Sets a token URI for a given token id.
  /// @dev Internal function to set the token URI for a given token. Reverts if the token ID does not exist
  /// @param tokenId uint256 ID of the token to set its URI
  /// @param uri string URI to assign
  function _setTokenURI(uint256 tokenId, string memory uri) internal {
      require(_exists(tokenId));
      _tokenURIs[tokenId] = uri;
  }

  /// @notice Mint a new employee card Consensual SBT token.
  /// @param _recipient Recipient address.
  /// @param _tokenURI The token URI.
  /// emit EmployeeCardMinted event when card is minted.
  function mint(address _recipient, string calldata _tokenURI) external onlyOwner {
    require(balanceOf(_recipient) == 0, "An employee can only have 1 token");

    uint256 tokenId = this.totalSupply() + 1; // Avoid using token id 0.
    _safeMint(_recipient, tokenId, BurnAuth.Both);

    require(_exists(tokenId), "EmployeeCard: token generation failed");
    _setTokenURI(tokenId, _tokenURI);

    emit EmployeeCardMinted(_recipient, tokenId);

    _approve(owner(), tokenId);
  }

  /// @notice Gets the employee card id.
  /// @param employee Employee address.
  /// @return The employee card id.
  function getEmployeeCardId(address employee) public view returns (uint256) {
    require(0 < this.balanceOf(employee), "EmployeeCard: This address doesn't have any employee card.");

    uint256 employeeTokenId = tokenOfOwnerByIndex(employee, 0);
    _requireMinted(employeeTokenId);

    return employeeTokenId;
  }

  /// @notice Returns if the SBT is still valid.
  /// @dev A valid token is a token without end time set
  /// @return True is it's still valid, false otherwise.
  function isTokenValid(uint256 tokenId) public view returns (bool) {
    _requireMinted(tokenId);

    return _tokenEndTimes[tokenId] == 0 || _tokenEndTimes[tokenId] >= block.timestamp;
  }

  /// @notice Gets an employee card SBT end time.
  /// @return End time in unix timestamp format. It returns 0 if the card doesn't have end time yet.
  function getEmployeeCardEndTime(uint256 tokenId) external view returns(uint256) {
     _requireMinted(tokenId);

     return _tokenEndTimes[tokenId];
  }

  /// @notice Invalidates an SBT token.
  /// @dev Saves the end date (in unix timestamp format) in _tokenEndTimes mapping.
  /// @param tokenId The token id to invalidate.
  function invalidateEmployeeCard(uint256 tokenId, uint256 endTime) external onlyOwner {
     _requireMinted(tokenId);
    require(endTime >= block.timestamp, "EmployeeCard: you must specify a end time in the future");
    _tokenEndTimes[tokenId] = endTime;

    emit EmployeeCardEnded(tokenId, endTime);
  }

  /// @notice Burns a card.
  /// @param employee Current holder of the card.
  function burnCard(address employee) external onlyOwner {
    uint256 employeeTokenId = getEmployeeCardId(employee);

    _burn(employeeTokenId);

    // Clear metadata (if any)
    if (bytes(_tokenURIs[employeeTokenId]).length != 0) {
        delete _tokenURIs[employeeTokenId];
    }
  }
}
